			console.log('dropIndex:', index)
			console.log('dragTask:', currentTaskRef.current)
			console.log('dropOnTask:', task)

			tasks.splice(currentIndex, 1);
			tasks.splice(dropIndex + 1, 0, task);
		}

	return (	
		<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>
						{tasks && tasks.map((task, index) => (
							<div className={css.card} > 
								<div 
								className={css.task}
								id={index}
								key={index}
								draggable={true}
								onDragStart={(e)=>dragStartHandler(e, title, task)}
								onDragEnd={(e)=>dragEndHandler(e)}
								onDragOver={(e)=>dragOverHandler(e)}
								onDragLeave={(e)=>dragLeaveHandler(e)}
								onDrop={(e)=>dropHandler(e, title, task, index)}
								>{task}</div>
								<Button 
									className={css['button-remove']} 
									onClick={()=> deleteTaskFromList(index)}
								>
									<CloseIcon />
								</Button>	
							</div> 
							))
						}	 
					</Scrollbars>
					<input 
							type='text' 
							placeholder='Write some task'
							onChange={(e) => setInputValue(e.target.value)}
							value={inputValue}
					>
					</input>
				</div>
				<div className={css.footer}>
					<button 
						className={css.button_add} 
						type='button' 
						onClick={()=>onClickAddTask(id, inputValue)}
					>+</button>
