import {Column} from "./column/Column";
import css from './board.module.scss';
import { useTasks } from '../../contexts/use-task';
import { useState } from 'react';

const [tasksList, setTasksList] = useState()

export const Board = () => {
	const [tasks, setTasks] = useState([])
	const [states] = useState([
		{id: 1, title: 'backlog', state: 'backlog'},
		{id: 2, title: 'ready', state: 'ready'},
		{id: 3, title: 'in progress', state: 'inProgress'},
		{id: 4, title: 'finished', state: 'finished'}
]);
	const [inputValue, setInputValue] = useState('');
	const [isLoaded, setLoaded] = useState(false);


	const onChangeImputValue = (event)=>{
		setInputValue(event.target.value)
	};
	const onClickAddTask= ()=>{
			if(inputValue && !tasks.includes(inputValue)){
				setTasks((prev)=>[...prev, inputValue])
				setInputValue('');
			}else{
				alert("Please write some task into input")
			} 
	};
	const deleteTaskFromList = (index)=>{
		setTasks(tasks.filter((_, _index) => _index != index));
	}
  return (
		<div className={css.board}>
			{states.map(
				(state, index) =>
					(<Column 
					key={index}
					id={state.id}
					title={state.title}
					state={state.state}
					tasks={tasks}
					isLoaded={isLoaded}
					onChangeImputValue={onChangeImputValue}
					onClickAddTask={onClickAddTask}
					deleteTaskFromList={deleteTaskFromList}
				/>)
			)}
		</div>
    )
}
