import { Scrollbars } from 'react-custom-scrollbars-2';
import { TaskProvider } from '../../../providers/task-provider';
import { useState } from 'react';
import { CloseIcon } from '../../shared/icons/CloseIcon';
import { Button } from '../../shared/buttons/button/Button';
import css from './column.module.scss';

export const Column = ({ title, state})=>{

	return (
		<TaskProvider>
<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>
							{tasks && tasks.map((task, index) => (
								<div className={css.card} key={index}> 
									<div className={css.card} >
										<span>{task}</span>
										<Button 
											className={css['button-remove']} 
											onClick={()=> deleteTaskFromList(index)}>
										 <CloseIcon />
										</Button>	
									</div>
								</div> 
								))
							}	 
						<input 
							type='text' 
							placeholder='Write some task'
							value={inputValue}
							onChange={onChangeImputValue}>
						</input>
					</Scrollbars>
				</div>
				<div className={css.footer}>
					<button 
						type='button' 
						onClick={onClickAddTask}
					>+</button>
					<span>Add task</span>
					{
						state.title !== 'backlog' &&
							(<div className={css['select-form']}>
								<select>
									<option></option>
									<option></option>
								</select>
								<button 
									type="button" 
									onClick={submitChoosenTask}
								>Select</button>
							</div>)
					}
				</div>
			</div>
		</div>
		</TaskProvider>

)}
