import {Column} from "./column/Column";
import css from './board.module.scss';
import { useTasks } from '../../contexts/use-task';
// 

export const Board = () => {
	const {states} = useTasks();
    return (
        <div className={css.board}>
            {states.map(
                (state, index) =>
										(<Column 
										key={index}
										id={state.id}
										title={state.title}
										state={state.state} 
									/>)
            )}
        </div>
    )
}
