import {Column} from "./column/Column";
import css from './board.module.scss';
import { useTasks } from '../../contexts/use-task';
import { useState } from 'react';



export const Board = () => {

	const [sections, setSections] = useState([
		{id: 1, title: 'backlog', tasks:[]},
		{id: 2, title: 'ready', tasks:[]},
		{id: 3, title: 'in progress', tasks:[]},
		{id: 4, title: 'finished', tasks:[]}
]);
	const [inputValue, setInputValue] = useState('');
	const [isLoaded, setIsLoaded] = useState(false);


	const onChangeImputValue = (event)=>{
		setInputValue(event.target.value)
	};
	const onClickAddTask= ()=>{
			if(inputValue && !tasks.includes(inputValue)){
				setTasks((prev)=>[...prev, inputValue])
				setInputValue('');
			}else{
				alert("Please write some task into input")
			} 
	};
	const deleteTaskFromList = (index)=>{
		setTasks(tasks.filter((_, _index) => _index != index));
	}

	useEffect(() => {
		if (isLoaded) {
				localStorage.setItem('tasks', JSON.stringify(tasks))
		}
}, [tasks, isLoaded])

useEffect(() => {
		const tasks = localStorage.getItem('tasks');
		if (tasks) {
				setTasks(JSON.parse(tasks))
		}
		setIsLoaded(true);
}, [])

  return (
		<div className={css.board}>
			{sections.map(
				(section, index) =>
					(<Column 
					key={index}
					id={section.id}
					title={section.title}
					tasks={tasks}
					isLoaded={isLoaded}
					onChangeImputValue={onChangeImputValue}
					onClickAddTask={onClickAddTask}
					deleteTaskFromList={deleteTaskFromList}
				/>)
			)}
		</div>
    )
}
