import { Scrollbars } from 'react-custom-scrollbars-2';
import { CloseIcon } from '../../shared/icons/CloseIcon';
import { Button } from '../../shared/buttons/button/Button';
import css from './column.module.scss';

export const Column = ({ 
		tasks, 
		inputValue, 
		title,  
		onChangeImputValue,
		onClickAddTask,
    deleteTaskFromList,
	})=>{
	return (	
		<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>
							{tasks && tasks.map((task, index) => (
								<div className={css.card} key={index}> 
										<span>{task}</span>
										<Button 
											className={css['button-remove']} 
											onClick={()=> deleteTaskFromList(index)}>
										 <CloseIcon />
										</Button>	
								</div> 
								))
							}	 

					</Scrollbars>
				</div>
				<div className={css.footer}>
					<button className={css.button_add} type='button' onClick={onClickAddTask}>+</button>
					<span>Add card</span>
					<div className={tasks.length === 0 ? css.hidden : ''}>
						<select>
							<option>Choose task</option>
							{
								tasks.map((item)=>
								(<option>{item}</option>))
							}				
						</select>
						<button className={css.throw_button}type="button">&#8921;</button>
					</div>
				</div>
			</div>
	</div>	
)}
