  return (
		<div className={css.board}>
			{sections.map(
				(section, index) =>
					(<Column 
					key={index}
					id={section.id}
					title={section.title}
					tasks={section.tasks}
					onClickAddTask={onClickAddTask}
					// deleteTaskFromList={deleteTaskFromList}
				/>)
			)}
		</div>
    )
}
