import { Scrollbars } from 'react-custom-scrollbars-2';
import { useState } from 'react';
import { Card } from './card/Card';
import css from './column.module.scss';

export const Column = ({id, state, title})=>{
	const [tasks, setTasks] = useState('')
	const [inputValue, setInputValue] = useState('');
	const onChangeImputValue = (event)=>{
		setInputValue(event.target.value)
	}
	const clickAddTask = ()=>{
		setTasks()
	}
	return (
		<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>

						<Card >Card in column</Card>
						<Card >Card in column</Card>
						<input 
							type='text' 
							placeholder='Write some task'
							value={inputValue}
							onChane={onChangeImputValue}>
						</input>
					</Scrollbars>
				</div>
				<div className={css.footer}>
					<button >+</button><span>Add task</span>
				</div>
			</div>

		</div>
	)
}
