import { Column } from "./column/Column";
import { useState } from 'react';
import css from './board.module.scss';

export const Board = () => {
	const [sections, setSections] = useState([
		{id: 0, title: 'backlog', tasks:["111"]},
		{id: 1, title: 'ready', tasks:["222"]},
		{id: 2, title: 'in progress', tasks:[]},
		{id: 3, title: 'finished', tasks:[]}
]);

	const onClickAddTask= (id, inputValue)=>{
		const currentSections = sections;
		console.log(currentSections,id);
		// if(inputValue && !currentSections[id].tasks.includes(inputValue)){
		// 	currentSections[id].tasks.push(inputValue);
		// 	setSections(currentSections)
		// 	}else{
		// 	alert("Please write some task into input")
		// }
		currentSections[id].tasks.push(inputValue);
		setSections(currentSections)
	};
	// const deleteTaskFromList = (index)=>{
	// 	tasks.filter((_, _index) => _index != index);
	// }

  return (
		<div className={css.board}>
			{sections.map(
				(section, index) =>
					(<Column 
					key={index}
					id={section.id}
					title={section.title}
					tasks={section.tasks}
					onClickAddTask={onClickAddTask}
					// deleteTaskFromList={deleteTaskFromList}
				/>)
			)}
		</div>
    )
}
