import { Scrollbars } from 'react-custom-scrollbars-2';
import { CloseIcon } from '../../shared/icons/CloseIcon';
import { Button } from '../../shared/buttons/button/Button';
import { useState } from'react';
import css from './column.module.scss';

export const Column = ({ 
	id,
	title
	})=>{
		const [inputValue, setInputValue] = useState('');
		const [tasks, setTasks] = useState([]);
		const [currentSection, setCurrentSection] = useState(null)
  	const [currentTask, setCurrentTask] = useState(null)

		const onClickAddTask= ()=>{
			if(inputValue && !tasks.includes(inputValue)){
				setTasks((prev)=>[...prev, inputValue])
				setInputValue('');
				}else{
				alert("Please write some task into input")
			}}
		const deleteTaskFromList = (index)=>{
			setTasks(tasks.filter((_, _index) => _index != index));	
		}
		function dragStartHandler(e, section, task){
			console.logsetCurrentSection(section);
			setCurrentTask(task);
		}
		function dragEndHandler(e){
			e.target.style.boxShadow = 'none'
		}
		function dragOverHandler(e){
			e.preventDefault();
			if(e.target.className == 'item'){
				e.target.style.boxShadow = ' 0 4px 3px gray'
			}
		}

		function dragLeaveHandler(e){
			e.target.style.boxShadow = 'none'
		}
		function dropHandler(e, section, task){
			e.preventDefault();
			const currentIndex = currentSection.tasks.indexOf(currentTask);
			currentSection.taskss.splice(currentIndex, 1);
			const dropIndex = section.tasks.indexOf(task);
			section.tasks.splice(dropIndex + 1, 0, currentTask);
			setSections(sections.map((b)=>{
				if(b.id === section.id){
					return section;
				}
				if(b.id === currentSection.id){
					return currentSection;
				}
				return b
			}))}
			// function dropCardHandler(e, board){
			// 	board.items.push(currentItem);
			// 	const currentIndex = currentBoard.items.indexOf(currentItem);
			// 	currentBoard.items.splice(currentIndex, 1);
			// 	setBoards(boards.map((b)=>{
			// 		if(b.id === board.id){
			// 			return board;
			// 		}
			// 		if(b.id === currentBoard.id){
			// 			return currentBoard;
			// 		}
			// 		return b
			// 	}))
			// }

	return (	
		<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>
						{tasks && tasks.map((task, index) => (
							<div className={css.card} > 
								<div 
								className='task'
								key={index}
								draggable={true}
								onDragStart={(e)=>dragStartHandler(e,section,task)}
								onDragEnd={(e)=>dragEndHandler(e)}
								onDragOver={(e)=>dragOverHandler(e)}
								onDragLeave={(e)=>dragLeaveHandler(e)}
								// onDrop={(e)=>dropHandler(e,section,task)}
								>{task}</div>
								<Button 
									className={css['button-remove']} 
									onClick={()=> deleteTaskFromList(index)}
								>
									<CloseIcon />
								</Button>	
							</div> 
							))
						}	 
					</Scrollbars>
					<input 
							type='text' 
							placeholder='Write some task'
							onChange={(e) => setInputValue(e.target.value)}
							value={inputValue}
					>
					</input>
				</div>
				<div className={css.footer}>
					<button 
						className={css.button_add} 
						type='button' 
						onClick={()=>onClickAddTask(id, inputValue)}
					>+</button>
					<span>Add card</span>
					 {/* <div className={tasks.length === 0 ? css.hidden : ''}>
						<select>
							<option>Choose task</option>
							{
								tasks.map((item, index)=>
								(<option key={index}>{item}</option>))
							}				
						</select>
						<button className={css.throw_button}type="button">&#8921;</button>
					</div>  */}
				</div>
			</div>
	</div>	
)}
