import { Scrollbars } from 'react-custom-scrollbars-2';
import { useState } from 'react';
import { Card } from './card/Card';
import css from './column.module.scss';

export const Column = ({id, state, title})=>{
	const [tasks, setTasks] = useState([null])
	const [inputValue, setInputValue] = useState('');
	const onChangeImputValue = (event)=>{
		setInputValue(event.target.value)
	}
	const onClickAddTask = ()=>{
		setTasks((task)=>{
				if(tasks.includes(task)){
					setTasks((prev)=>prev.filter((_task)=>task.toLowerCase() !== _task.toLowerCase()))
				}else{
					setTasks((prev)=>[...prev, task])
				}
				console.log(tasks)
			})	


	}
	return (
		<div className = {css.column}>
			<div className={css.header}>{title}</div>
			<div className={css.wrapper}>
				<div className={css.body}>
					<Scrollbars autoHeight autoHeightMax={500}>
						{/* {
							tasks && tasks.map((task)=>)
						} */}
						<Card >Card in column</Card>
						<Card >Card in column</Card>
						<input 
							type='text' 
							placeholder='Write some task'
							value={inputValue}
							onChange={onChangeImputValue}>
						</input>
					</Scrollbars>
				</div>
				<div className={css.footer}>
					<button onClick={onClickAddTask}>+</button>
					<span>Add task</span>
				</div>
			</div>

		</div>
	)
}
